import './Form.css';
import {useState} from 'react';

const Form = ({getRandomPrompt, getFilteredPrompt, faviconRandomiser}) => {
    const [discipline, setDiscipline] = useState("");
    const [maxTime, setMaxTime] = useState(30);
    const [difficulty, setDifficulty] = useState(5);

    const handleDisciplineChange = (event) => {
        setDiscipline(event.target.value);
    }

    const handleMaxTimeChange = (event) => {
        setMaxTime(event.target.value);
    }

    const handleDifficultyChange = (event) => {
        setDifficulty(event.target.value);
    }

    const handleFormSubmission = (event) => {
        event.preventDefault();
        const promptRequest = {
            discipline: discipline,
            minsToComplete: maxTime,
            difficulty: difficulty
        }
        // submit and trigger the function that talks to api
        getFilteredPrompt(promptRequest);
        // reset all the states
        setDiscipline("");
        setMaxTime(30);
        setDifficulty(5);
        () => faviconRandomiser();

    }

    return (
        <>
        <div className='form-container'>
            <form onSubmit={handleFormSubmission}>
                <div className="form-element">
                    <label className="form-label">Discipline:</label>
                        <select name="discipline" id="discipline" onChange={handleDisciplineChange}>
                                    <option value="">-Select an option-</option>
                                    <option value="writing">Writing</option>
                                    <option value="programming">Programming</option>
                                    <option value="art">Art</option>
                                    <option value="photography">Photography</option>
                        </select>
                </div>
                <div className="form-element">
                    <label className="form-label">Time:
                    <input type="number" id="max-time" value={maxTime} onChange={handleMaxTimeChange} />
                    </label>
                </div>
                <div className="form-element">
                    <label className="form-label">Difficulty:
                    <input type="number" id="difficulty" value={difficulty} onChange={handleDifficultyChange} min="1" max="5"/>
                    </label>
                </div>
                <div className="form-element">
                    <input type="submit" value="Get a Prompt"></input>
                </div>
            </form>

            
            
        </div>
        <div className="randomButton">
            <button onClick={getRandomPrompt}>Random!</button>
        </div>
        </>
    )
}

export default Form;
